            items.forEach { item ->
                if (item.isCentralButton) {
                    Spacer(modifier = Modifier.weight(0.5f))

                    CustomElevatedFab(
                        icon = item.unselectedIcon, // Central button's icon doesn't change
                        isSelected = currentRoute == item.route,
                        fabSize = fabSize,
                        iconSize = fabIconSize,
                        onClick = {
                            if (currentRoute != item.route) {
                                onNavigateToUpload()
                            }
                        }
                    )

                    Spacer(modifier = Modifier.weight(0.5f))
                } else {
                    CustomBottomNavItem(
                        item = item,
                        isSelected = currentRoute == item.route,
                        iconSize = regularIconSize,
                        onClick = {
                            if (currentRoute != item.route) {
                                when (item.route) {
                                    "dashboard" -> onNavigateToDashboard()
                                    "past_records" -> onNavigateToPastRecords()
                                }
                            }
                        },
                        modifier = Modifier.weight(1f)
                    )
                }
            }
        }
    }
}

@Composable
fun CustomBottomNavItem(
    item: BottomNavItem,
    isSelected: Boolean,
    onClick: () -> Unit,
    modifier: Modifier = Modifier,
    iconSize: Int = 24
) {
    Column(
        modifier = modifier
            .clickable(onClick = onClick)
            .padding(vertical = 8.dp),
        horizontalAlignment = Alignment.CenterHorizontally,
        verticalArrangement = Arrangement.Center
    ) {
        val iconToDisplay = if (isSelected) item.selectedIcon else item.unselectedIcon
        Icon(
            painter = painterResource(id = iconToDisplay),
            contentDescription = null,
            tint = if (isSelected) Color.Black else Color.Black,
            modifier = Modifier.size(iconSize.dp)
        )
    }
}

@Composable
fun CustomElevatedFab(
    icon: Int,
    isSelected: Boolean,
    onClick: () -> Unit,
    fabSize: Int = 64,
    iconSize: Int = 28,
    strokeColor: Color = Color.White,
    strokeWidth: Dp = 2.dp
) {
    Card(
        modifier = Modifier
            .size(fabSize.dp)
            .offset(y = (-20).dp)
            .border(
                width = strokeWidth,
                color = strokeColor,
                shape = CircleShape
            ),
        shape = CircleShape,
        colors = CardDefaults.cardColors(containerColor = Color.Black),
        elevation = CardDefaults.cardElevation(defaultElevation = 10.dp)
    ) {
        Box(
            modifier = Modifier
                .fillMaxSize()
                .clickable(onClick = onClick),
            contentAlignment = Alignment.Center
        ) {
            Icon(
                painter = painterResource(id = icon),
                contentDescription = null,
                tint = Color.White,
                modifier = Modifier.size(iconSize.dp)
            )
        }
    }
}

