@Composable
fun RecordCard(record: ImageData, onCardClick: (ImageData) -> Unit) {
    // Keep fixed sizes for the card and image as per the previous successful iteration
    val cardSize = 180.dp
    val imageHeight = 100.dp

    Card(
        modifier = Modifier
            .width(cardSize) // Fixed width for each card
            .height(cardSize) // Fixed height for each card, making it square
            .clickable { onCardClick(record) }, // Make entire card clickable
        shape = RoundedCornerShape(16.dp),
        colors = CardDefaults.cardColors(containerColor = Color(0xFF1C1C1E)),
        elevation = CardDefaults.cardElevation(defaultElevation = 4.dp)
    ) {
        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(12.dp),
            horizontalAlignment = Alignment.Start,
            verticalArrangement = Arrangement.SpaceBetween
        ) {
            // Image Box with fixed height
            Box(
                modifier = Modifier
                    .fillMaxWidth()
                    .height(imageHeight)
                    .clip(RoundedCornerShape(8.dp))
                    .background(Color.Black),
                contentAlignment = Alignment.Center
            ) {
                Image(
                    painter = rememberAsyncImagePainter(model = record.url),
                    contentDescription = "Brain Scan Image",
                    modifier = Modifier.fillMaxSize(),
                    contentScale = ContentScale.Fit,
                    alignment = Alignment.Center
                )
            }

            // Text elements
            val tumorType = record.information?.tumor_type?.replaceFirstChar { 
                if (it.isLowerCase()) it.titlecase(Locale.getDefault()) else it.toString() 
            } ?: run {
                // Fallback: Show "Analysis Pending" instead of N/A for missing predictions
                "Analysis Pending"
            }

            Text(
                text = tumorType,
                color = if (tumorType == "Analysis Pending") Color.Gray else Color.White,
                fontSize = 16.sp,
                fontWeight = FontWeight.Bold,
                maxLines = 1,
                overflow = TextOverflow.Ellipsis
            )

            val confidenceText = record.information?.confidence?.let { 
                "%.2f".format(it * 100) + "%" 
            } ?: run {
                // Show different message instead of N/A
                if (record.information == null) "Pending" else "N/A"
            }

            Text(
                text = confidenceText,
                color = Color.LightGray,
                fontSize = 12.sp
            )

            // Date and View > in a Row
            val inputFormat = SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss.SSSSSSXXX", Locale.getDefault())
            val outputFormat = SimpleDateFormat("MMMM d, yyyy", Locale.getDefault())
            val date = try {
                record.uploaded_at?.let { inputFormat.parse(it) }
            } catch (_: Exception) {
                null
            }
            val formattedDate = date?.let { outputFormat.format(it) } ?: "N/A"

            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = formattedDate,
                    color = Color.Gray,
                    fontSize = 10.sp
                )
                Text(
                    text = "View >",
                    color = Color.White,
                    fontSize = 12.sp,
                    fontWeight = FontWeight.SemiBold,
                    modifier = Modifier.padding(4.dp)
                )
            }
        }
    }
}

package com.example.neuroview.screens

import android.net.Uri
import androidx.compose.foundation.Image
import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.rememberScrollState
